import { Component } from '@angular/core';
import { Store } from 'redux';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  contador:number;

  constructor( private store: Store){
    this.contador = 10;
  }

  incrementar(){
    this.contador ++;
  }

  decrementar(){
    this.contador --;
  }


}
