import { createAction, props } from '@ngrx/store';

export const incrementar = createAction('[Contrador] Incrementar');
export const decrementar = createAction('[Contrador] Decrementar');

export const multiplicar = createAction(
    '[Contrador] Multiplicar',
    props<{numero: number}>()
    );

export const dividir = createAction(
    '[Contrador] Dividir',
    props<{numero: number}>()
    );
